import React from 'react';
import { Text, StyleSheet, View, TextInput, SafeAreaView, Button, Pressable, Dimensions, Alert, Image, ScrollView, } from 'react-native';
import logo from './../assets/FireUpLogo.png';
import { StackActions} from "react-navigation";
import {
    styles
} from './../Styles/styles'
import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph
} from 'react-native-chart-kit'

const MainScreen = ({navigation}) => {
    navigation.dispatch(
        StackActions.reset()
    )
    const data = [0.6, 0.6, 0.6, 0.6];
    const chartConfig = {
        backgroundColor: '#ACDEAA',
        backgroundGradientFrom: '#ACDEAA',
        backgroundGradientTo: '#FFB699',
        alignItems:'center',
        decimalPlaces: 2, // optional, defaults to 2dp
        color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
        style: {
            borderRadius: 16
        }
    }
    const pieData = [
        { name: 'to FIRE', population: 60, color: '#ACDEAA', legendFontColor: '#7F7F7F', legendFontSize: 15 },
        { name: '', population: 40, color: 'transparent', legendFontColor: 'transparent', legendFontSize: 15 },
    ]
    return(
        <SafeAreaView style={styles.backgroundLayoutView}>
            <ScrollView>
                <View>
                    <Text style={styles.headerText}>
                        Line Chart
                    </Text>
                    <LineChart
                        data={{
                            labels: [ 600, 'One', 'Two', 'Five', 'Three', 'Four', 'Six'],
                            datasets: [{
                                data: [
                                    0,
                                    1,
                                    2,
                                    5,
                                    3,
                                    4,
                                    6
                                ]
                            }]
                        }}
                        width={Dimensions.get('window').width} // from react-native
                        height={200}
                        chartConfig={{
                            backgroundColor: '#ACDEAA',
                            backgroundGradientFrom: '#ACDEAA',
                            backgroundGradientTo: '#FFB699',
                            decimalPlaces: 2, // optional, defaults to 2dp
                            color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
                            style: {
                                borderRadius: 16
                            }
                        }}
                        bezier
                        style={{
                            marginVertical: 8,
                            borderRadius: 16
                        }}
                    />

                    <ProgressChart
                        data={data}
                        width={Dimensions.get('window').width} // from react-native
                        height={200}
                        chartConfig={chartConfig}
                        style={{
                            marginVertical: 8,
                            borderRadius: 16
                        }}
                    />
                    <PieChart
                        data={pieData}
                        width={Dimensions.get('window').width} // from react-native
                        height={220}
                        chartConfig={chartConfig}
                        accessor="population"
                        backgroundColor="transparent"
                        paddingLeft="15"
                    />
                </View>
            </ScrollView>
        </SafeAreaView>
    );
}

export default MainScreen;
